import { Router } from '@angular/router';
import { getProductData, addCart } from '../store/product.actions';
import { AppState } from '../../../store/app.state';
import { Store } from '@ngrx/store';
import { Component, OnDestroy, OnInit } from '@angular/core';
import { loadAllProducts } from '../store/product.actions';
import { Observable } from 'rxjs';
import { getCartData, getProducts } from '../store/product.selector';
import { MediaChange, MediaObserver } from '@angular/flex-layout';
import { GetProdData } from 'src/app/model/getProductData';

@Component({
  selector: 'app-show-products',
  templateUrl: './show-products.component.html',
  styleUrls: ['./show-products.component.css']
})
export class ShowProductsComponent implements OnInit, OnDestroy {

  mediaSub: any;
  deviceXs: any;
  userId: string;
  products: Observable<GetProdData[]> | any;


  constructor(private store: Store<AppState>,
              public mediaObserver: MediaObserver,
              private router: Router) {
    this.userId = JSON.parse(localStorage.getItem('uid') || '{}');
  }

  ngOnInit(): void {

    //this is for responsiveness
    this.mediaSub = this.mediaObserver.media$.subscribe((res: MediaChange) => {
      this.deviceXs = res.mqAlias === "xs" ? true : false;
    })

    //calling an action to get all products
    this.store.dispatch(loadAllProducts());

    //it will get a data which is storeed in "store"
    this.products = this.store.select(getProducts);
  }

  ngOnDestroy() {
    this.mediaSub.unsubscribe();
  }

  addToCart(id: number) {
   //it will call an action to add a product in a cart
    this.store.dispatch(getProductData({ id: id }));
  }
}
<app-nav-bar [deviceXs]="deviceXs"></app-nav-bar>

<div fxLayout="row" fxLayout.xs="column">
    <div fxFlex="100%" fxFlex.xs="100%">

        <div class="content">
            <div fxLayout="row wrap" fxLayoutGap="16px grid">
                <div fxFlex=25% fxFlex.xs=100% fxFlex.sm=50% fxFlex.md=50% *ngFor="let products of (products | async)">
                    <mat-card class="mat-elevation-z4">
                        <mat-card-header>
                            <mat-card-title>{{products.productName}}</mat-card-title>
                        </mat-card-header>
                        <br>
                        <img mat-card-image src="{{products.image}}" alt="">
                        <mat-card-content>
                            <p>
                                Price: {{products.price}} INR
                            </p>
                        </mat-card-content>
                        <mat-card-footer fxLayoutAlign="space-between start">
                            <a [routerLink]="['/product/moreinfo/',products.id]">More Info</a>
                            <!-- <button mat-raised-button (click)="addToCart(products.id)"><mat-icon>shopping_cart</mat-icon> Add To Cart</button> -->

                        <lib-buttion [buttonText]="'Add to Cart'" [iconName]="'shopping_cart'" (click)="addToCart(products.id)"></lib-buttion>
                        </mat-card-footer>
                    </mat-card>
                </div>
            </div>
        </div>
    </div>
</div>
